import { useEffect, useState } from "react"

import { getQuestions, serverURL } from '../integration'

const useFetch = (url, userID) => {

    const [data, setData] = useState(null)
    const [isLoading, setIsLoading] = useState(true)
    const [numberQuestions, setNumberQuestions] = useState(0)

    useEffect(() => {
        fetch(url)
        .then(res => {
            return res.json()
        })
        .then(data => {
            setData(data[userID])
            setIsLoading(false)
            setNumberQuestions(Object.keys(data[userID]).length)
        })
        .catch(err => {
            console.log(err.message)
        })
    }, [url, userID])

    return {data, isLoading, numberQuestions}
}

export default useFetch;
