const Answer = ( {className,type, options} ) => {

    const getDropdownOptions = (num) => {
        const array = []
        for(var i = 0; i < num; i++){
          let a = i;
          array.push(
            <option key={a}>{options[a]}</option>)
        }
        return array
    }

    const getMultipleOptions = (num) => {
        const array = []
        for(var i = 0; i < num; i++){
          let a = i;
          array.push(
                <div key={a}>
                    <input
                    type="checkbox"
                    id={a}
                    />
                    <p>{options[a]}</p>
                </div>
          )
        }
        return array
    }

    const findCorrectArea = (type) => {
        if (type === "text") {
            return <textarea name="" id="" cols="30" rows="10"></textarea>
        } else if (type === "dropdown") {
            const numberOptions = Object.keys(options).length
            return <select name="" id="">
                {getDropdownOptions(numberOptions)}
            </select>
        } else if (type === "short") {
            return <div className="short-text">
                        <input type="text"></input>
                    </div>
        } else if (type === "select") {
            const numberOptions = Object.keys(options).length
            return <div className="multiple-options">
                {getMultipleOptions(numberOptions)}
            </div>
        }
    }

    return ( 
        <div className="answer">
            {findCorrectArea(type)}
        </div>
     );
}

export default Answer;
