import styled from "styled-components";

export const StyledShortAnswer = styled.div`
    height: 45px;
    width: 100%;
    input {
        height: 43px;
        border-radius: 6px;
        background-color: rgb(247, 233, 206);
        border: 0;
        width: 80%;
    }
`
