import './App.css';
import { StyledMainCard } from './Components/MainCard-style';
import { useState, useEffect } from "react";
import { getQuestions } from './integration.ts';
import * as Yup from 'yup';
import { useFormik } from 'formik';

function App() {
  const [isLoading, setIsLoading] = useState(true)
  const [userID, setUserID] = useState('0')
  const [data, setData] = useState(null)
  const [surveyID, setSurveyId] = useState(null)
  const [numberQuestions, setNumberQuestions] = useState(0)

  const restartFunction = (mood) => {
    setIsLoading(mood)
  }

  window.addEventListener('message', function(event) {
    if (event.data.source === "survey-parent" ) {
      setUserID(event.data.value)
      getQuestions(event.data.value)
      .then(dataFetched => {
        setData(dataFetched.questions)
        setSurveyId(dataFetched.surveyId)
        setIsLoading(false)
        if (dataFetched.questions) {
          setNumberQuestions(dataFetched.questions.length)
        } else {
          setNumberQuestions(0)
        }
      })
    }
  }, false);

  function createFormik() {
    var valueDict = {}
    for (let i = 0; i<numberQuestions; i++) {
        valueDict[i] = ""
    }
    var validationDict = {
        "SHORT_ANSWER" : Yup.string()
            .max(50, "Must be 50 characters of less."),
        "LONG_ANSWER" : Yup.string()
            .max(1000, "Must be 10000 characters of less."),
    }
    var schema = {}
    for (let i = 0; i < numberQuestions; i++) {
        schema[i] = validationDict[data[i].questionType]
    }

    const formik = useFormik({
        initialValues : valueDict,
        validationSchema : Yup.object().shape(schema),
    })
    return formik
  }
  const [formik, setFormik] = useState(0)
  useEffect(() => {
    setFormik(createFormik())
  }, [numberQuestions]);


  return (

      <div className='main-div'>
        {<StyledMainCard isLoading={isLoading} data={data} numberQuestions={numberQuestions} userID={userID} surveyID={surveyID} restartFunction={restartFunction} formik = {formik}></StyledMainCard>}
      </div>
  );
}

export default App;
