import './ContentCard.css';
import { useState } from 'react'
import { StyledAnswer } from './Answer-style';
import { StyledButton } from './Button-style'

const ContentCard = ({data, numberQuestions}) => {

    const [currentQuestion, setCurrentQuestion] = useState(0)

    const getButtonsUsingForLoop = (num) => {
        const array = []
        for(var i = 0; i < numberQuestions; i++){
          let a = i;
          array.push(
            <button
                onClick={()=>{
                    setCurrentQuestion(a);
                }}
                key={i}
                >Q{i+1}
            </button>)
        }
        return array
    }

    return ( 
        <div>
            <div className="container">
                <div className="left">
                    {getButtonsUsingForLoop(numberQuestions)}
                </div>
                <div className="right">
                    <p id='question-text'>{data[currentQuestion].question}</p>
                    <p id='question-subtext'>{data[currentQuestion].additional}</p>
                    <StyledAnswer type={data[currentQuestion].type} options={data[currentQuestion].options}></StyledAnswer>
                    <div className="progress-buttons">
                        <StyledButton right={"9px"} width={"80%"}
                            onClick = {
                                () => {
                                    setCurrentQuestion(Math.max(0,currentQuestion-1))
                                }
                            }
                        ></StyledButton>
                        <StyledButton right={"9px"}
                            onClick = {
                                () => {
                                    setCurrentQuestion(Math.min(numberQuestions-1,currentQuestion+1))
                                }
                            }
                        ></StyledButton>

                    </div>
                </div>
            </div>
            <div className="button-holder">
                <button>Submit</button>
            </div>
        </div>

     );
}

export default ContentCard;
